/**
 * 1.追加文件内容
 *
 * filePath:     C:\Users\1\Desktop\132\123.txt
 * text:         追加的内容 或者写入的内容
 */
fun fileAppendText(filePath:String,text:String){

    //1.创建文件  这里
    val file = File(filePath)

    //2.如果文件不存在,则创建文件
    if (!file.exists()) {
        file.createNewFile()
    }

    //3.以追加模式打开文件 不要换行
//    file.appendText(text, Charset.forName("UTF-8"))

    //4.以追加模式打开文件 加入换行
    file.appendText("$text\n", Charset.forName("UTF-8"))
}


/**
 * 2.消除所有的换行标记  一般用于将获取到网页内容 规整为一行
 */
fun setTextToOneLine(str:String):String{
    return str.replace(Regex("\\s+"), "")
}

