fun getBetweenT1AndT2(text: String,t1:String,t2:String):String{
    return text.substring(text.indexOf(t1) + t1.length, text.lastIndexOf(t2))
}

/**
 * 4.将一个txt文本的每一行操作之后  输出到另一个txt文本
 *
 * path1: C:\Users\1\Desktop\字典.txt        目标文件
 * path2: C:\Users\1\Desktop\1\$tag.txt     输出的文件 这里可以通过循环修改目标文件夹
 */
fun file1ToFile2(path1:String,path2:String){
    //1.先找到两个文件
    val inputFile = File(path1)
    val outputFile = File(path2)


    //2.读取目标文件的内容
    val inputLines = mutableListOf<String>()
    inputFile.bufferedReader().use { reader ->
        reader.lineSequence().forEach { line ->
            inputLines.add(line)
        }
    }

    //3.对目标文件的每一行进行操作
    val outputLines = inputLines.map { line ->
        // 在这里对 line 进行操作
        //1.比如只取<之前的东西
        extractTextBeforeDelimiter(line, "<").trim()
        //2.等等等等
    }


    //4.将操作后的内容写入输出文件
    outputFile.bufferedWriter().use { writer ->
        outputLines.forEach { line ->
            writer.write(line)
        //是否加入一个换行
        //writer.newLine()
        }
    }

    //一个文件处理完成
    println("文件处理完成!")
}

/**
 * 5.将所有txt文件内容合并输出到一个txt文件
 *
 * directoryPath:  C:\Users\1\Desktop\1
 * filePath :  C:\Users\1\Desktop\file.txt
 */
@RequiresApi(Build.VERSION_CODES.O)
fun allFileToOneFile(directoryPath: String,filePath: String) {

    // 创建输出文件
    val outputFilePath = Paths.get(filePath)
    Files.createFile(outputFilePath)

    // 遍历源目录下的所有 TXT 文件
    val txtFiles = Files.walk(Paths.get(directoryPath))
        .filter { Files.isRegularFile(it) && it.toString().endsWith(".txt") }
        .toList()

    // 将所有文件内容写入输出文件
    BufferedWriter(FileWriter(filePath, true)).use { writer ->
        for (file in txtFiles) {
            writer.write(file.fileName.toString().removeSuffix(".txt")+"|")
            Files.readAllLines(file).forEach { line ->
                writer.write("$line\n")
            }
        }
    }

    println("文件内容已成功输出至 $filePath")

}
